mapper.select:
SELECT COUNT(*)
  FROM tb_user_car
 WHERE user_id = #{user_id}

SELECT car_id, user_id, car_plate, car_mf, car_model, car_year, car_socket, reg_dt, upd_dt
  FROM tb_user_car
 WHERE user_id = #{user_id}

mapper.update:
UPDATE tb_user_car
car_plate = #{car_plate}, car_mf = #{car_mf}, car_model = #{car_model}, car_year = #{car_year}, car_socket = #{car_socket}, upd_dt = sysdate()
WHERE car_id = #{car_id}
