mapper.select:
SELECT user_id, user_tp, login_id, user_nicknm, user_nm, user_phone
  FROM tb_user
 WHERE user_id = #{user_id}

mapper.update:
UPDATE tb_user
user_nicknm = #{user_nicknm}, user_nm = #{user_nm}, user_phone = #{user_phone}, upd_user = #{login_id}, upd_dt = sysdate()
WHERE user_id = #{user_id}
