mapper.update:
UPDATE tb_reservation
SET res_status = 'C'
WHERE res_id = #{resId}

mapper.select:
SELECT res_status
FROM tb_reservation
WHERE res_id = #{resId}

SELECT * FROM (SELECT tr.res_id, tr.charger_id, tr.user_id, tr.res_nm, tr.res_tel, tr.res_email, tr.res_date, tr.res_start_time, tr.res_end_time, tr.pay_total_hour, tr.res_status, tr.use_status, tr.reg_dt, (select tuc.car_model from tb_user_car tuc where tr.user_id = tuc.user_id) as car_model, (select tpc.addr||' '||tpc.addr_detail from tb_private_charger tpc where tr.charger_id = tpc.charger_id) as chager_addr, ROW_NUMBER() OVER(ORDER BY reg_dt DESC) AS ROW_NUM FROM tb_reservation tr
tr.charger_id = (SELECT charger_id FROM tb_private_charger WHERE user_id = #{user_id})
AND tr.res_nm LIKE '%'||#{searchValue}||'%'
AND car_model LIKE '%'||#{searchValue}||'%'
tr.user_id = #{user_id}
AND tr.chager_addr LIKE '%'||#{searchValue}||'%'
) R WHERE 1=1 AND R.ROW_NUM BETWEEN #{begin} AND #{end}

SELECT COUNT(*) AS TOTALCOUNT FROM tb_reservation
charger_id = (SELECT charger_id FROM tb_private_charger WHERE user_id = #{user_id})
user_id = #{user_id}
